import { Injectable } from '@angular/core';
import { Subject } from 'rxjs';
import { Router } from '@angular/router';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  /**
   * @description: 
   */
  private config = {
    signin : {
      redirectToPathname: ['/', 'dashboard'],
      eventAfterSignin: () => {
        this.router.navigate([this.config.signin.redirectToPathname]);
      },
    },
    signup: {
      redirectToPathname: ['/', 'dashboard'],
      eventAfterSignup: () => {
        this.router.navigate([this.config.signup.redirectToPathname]);
      },
    },
    forget_password: {
      redirectToPathname: ['/', 'dashboard'],
      eventAfterForgetPassword: () => {
        this.router.navigate([this.config.forget_password.redirectToPathname]);
      },
    },
  };

  /**
   * @description: 
   */
  constructor(
    private router: Router,
  ) { }

  /**
   * @description: 
   */
  public get_config(config_name: string): any {
    // return this.config[config_name];
  }
}
