import { Injectable } from '@angular/core';
import { Subject } from 'rxjs';
import { Router } from '@angular/router';
import { 
  ConfigSignin, 
  ConfigSignup, 
  ConfigForgetPassword 
} from '../types'; 


@Injectable({
  providedIn: 'root'
})
export class AuthService {
  /**
   * @description: 
   */
  public config__signin: ConfigSignin = {
    redirectToPathname: ['/', 'dashboard'],
    eventAfterSignin: () => {
      this.router.navigate([this.config.signin.redirectToPathname]);
    },
  };

  /**
   * @description: 
   */
  public config__signup: ConfigSignup = {
    redirectToPathname: ['/', 'dashboard'],
    eventAfterSignup: () => {
      this.router.navigate([this.config.signup.redirectToPathname]);
    },
  };

  /**
   * @description: 
   */
  public config__forget_password: ConfigForgetPassword = {
    redirectToPathname: ['/', 'dashboard'],
    eventAfterForgetPassword: () => {
      this.router.navigate([this.config.forget_password.redirectToPathname]);
    },
  },

  private config = {
    signin : {
      redirectToPathname: ['/', 'dashboard'],
      eventAfterSignin: () => {
        this.router.navigate([this.config.signin.redirectToPathname]);
      },
    },
    signup: {
      redirectToPathname: ['/', 'dashboard'],
      eventAfterSignup: () => {
        this.router.navigate([this.config.signup.redirectToPathname]);
      },
    },
    forget_password: {
      redirectToPathname: ['/', 'dashboard'],
      eventAfterForgetPassword: () => {
        this.router.navigate([this.config.forget_password.redirectToPathname]);
      },
    },
  };



  /**
   * @description: 
   */
  constructor(
    private router: Router,
  ) { }
}
